CREATE DEFINER=`root`@`%` FUNCTION `get_TAT`(val datetime) RETURNS varchar(200) CHARSET latin1
BEGIN
IF (val IS NULL) THEN
RETURN null;
END IF;
RETURN (select CONCAT(
				FLOOR(TIME_FORMAT(SEC_TO_TIME((select TO_SECONDS(now())) - TO_SECONDS(val)), '%H') / 24), ' days ',
				MOD(TIME_FORMAT(SEC_TO_TIME((select TO_SECONDS(now())) - TO_SECONDS(val)), '%H'), 24), ' hours ',
				TIME_FORMAT(SEC_TO_TIME((select TO_SECONDS(now())) - TO_SECONDS(val)), '%i minutes %s seconds')
				));
END ;;
