CREATE DEFINER=`root`@`%` PROCEDURE `getMemoData`(IN OBLIGOR int )
begin

SET @seq := 0;
SELECT concat(@seq := FLOOR(@seq + 1),"." ,a.FIRST_NAME," ",a.MID_NAME," ",a.LAST_NAME," ","-"," ",a.SHARES,"%") AS MainSponsor ,
	(select GROUP_NAME from group_basic_detail where GROUP_BD_ID = b.OBLIGOR_GROUP_ID limit 1 )'GROUP_NAME'  ,
    b.GBRR, 
    ifnull((select LAST_ACT_DATE from  approval_activity_log where OBLIGOR_ID = b.OBLIGOR_ID and ACT_STATUS = 'A' limit 1 ),"")'FY',
	(select LEGAL_CONS_NAME from legal_constitution where LEGAL_CONS_ID = b.LEGAL_CONS_ID limit 1 )'LegalStatus' ,
	(select concat(FIRST_NAME," ",LAST_NAME) from employee_credentials where LOGIN_ID  = b.CREATED_BY limit 1) as 'RM' ,
	a.RELATED_PARTY , a.IS_PEP ,
    (select OBS_NAME from operational_business_segment where OBS_ID = b.OBS_ID limit 1)'OBS_NAME', 
    (select  BRANCH_NAME from branch where BRANCH_ID = b.BRANCH_ID limit 1)'BRANCH_NAME'   , 
	( select REGION_NAME from region where REGION_ID = b.REGION_ID limit 1)'REGION_NAME',
	(select a.REGULARITY_SEC_NAME from  regularity_sector_sub a , obligor_business_details b  where a.REGULARITY_SEC_ID = b.REGULARITY_SEC_ID and b.OBLIGOR_ID = b.OBLIGOR_ID and b.OBLIGOR_ID = OBLIGOR  limit 1) 'REGULARITY_SEC_NAME',
    (select a.REGULARITY_IND_NAME from regularity_industry a,  regularity_sector_sub b , obligor_business_details c  where b.REGULARITY_SEC_ID = c.REGULARITY_SEC_ID and c.OBLIGOR_ID = b.OBLIGOR_ID  and b.OBLIGOR_ID = OBLIGOR and a.REGULARITY_IND_ID = c.REGULARITY_IND_ID  limit 1) 'REGULARITY_IND_NAME',
    (select a.REGULARITY_IND_CODE from regularity_industry a,  regularity_sector_sub b , obligor_business_details c  where b.REGULARITY_SEC_ID = c.REGULARITY_SEC_ID and c.OBLIGOR_ID = b.OBLIGOR_ID and a.REGULARITY_IND_ID = c.REGULARITY_IND_ID  and b.OBLIGOR_ID = OBLIGOR  limit 1) 'REGULARITY_IND_CODE',
    (select RBS_NAME from regulatory_business_segment a ,obligor_business_details b where a.RBS_ID=b.RBS_ID and b.OBLIGOR_ID = b.OBLIGOR_ID and b.OBLIGOR_ID = OBLIGOR  order by APPROVAL_ACT_ID desc limit 1 ) 'RBS_NAME' , 
    ifnull((select LAST_ANNUAL_DATE from  approval_activity_log where OBLIGOR_ID = b.OBLIGOR_ID and ACT_STATUS = 'A' order by APPROVAL_ACT_ID desc limit 1 ),'')'EXISTING_EXPIRY_DATE'
FROM management_information a  ,  obligor_information b  
where a.OBLIGOR_ID = b.OBLIGOR_ID 
and a.OBLIGOR_ID = OBLIGOR ;  

end ;;
