CREATE DEFINER=`root`@`%` PROCEDURE `GetNominies`(IN WF_PROFILE_ID int ,OBS_ID int )
BEGIN
DECLARE Act_Taken char(5) default null;
DECLARE New_Wf_Id int default null;

select Action_Taken,To_Profile 
   into Act_Taken,New_Wf_Id  
       from clos_dev.wf_decidingactions  where WF_ACTION_ID = WF_PROFILE_ID;

if(( select count(*) from clos_dev.wf_user_package_approval_log a
		    where a.Approval_Act_Id = OBS_ID and a.NEW_WF_PROFILE_ID = New_Wf_Id ) > 0 and Act_Taken = 'B')
    then 
    begin 
   select e.E_ID ,CONCAT(e.FIRST_NAME, " ", e.LAST_NAME) 'Name'     
      from clos_dev.wf_user_package_approval_log a 
      inner join clos_dev.employee_credentials e on  e.E_ID  = a.E_ID and e.is_resign = 0
	where a.Approval_Act_Id = OBS_ID and a.NEW_WF_PROFILE_ID = New_Wf_Id order by a.LOG_ID desc limit 1;
        end ;
        else  
        begin 
   select e.E_ID ,CONCAT(e.FIRST_NAME, " ", e.LAST_NAME) 'Name'     
      from clos_dev.wf_userprofile_config a 
      inner join clos_dev.employee_profile b  on  a.UserProfile  = b.USER_PROFILE_ID and b.status = 'A'  and b.IS_DEFAULT = 1
      inner join clos_dev.employee_credentials e on  e.E_ID  = b.E_ID and e.is_resign = 0
        where a.WF_PROFILE_ID = New_Wf_Id  ;
        end ;
        end if ;
END ;;
