CREATE DEFINER=`root`@`%` PROCEDURE `getServicingData`(IN ASSIGNED_EMP_ID INT)
BEGIN
set @activity_name = (select activity_name from wf_activity_setup where is_starter= 1 limit 1 ) ;

SELECT  b.OBLIGOR_CODE'RequestCode', b.OBLIGOR_CODE, b.OBLIGOR_ID, b.OBLIGOR_NAME 'CustomerName', DATE_FORMAT(a.CREATED_DATE,'%d %b %Y')'CREATEDDATE', a.CREATED_BY 'CREATEDBY', c.SHORT_DESC 'RequestStatus',
c.ACTIVITY_NAME as 'RequestType', f.BRANCH_NAME, g.OBS_NAME, b.CIF,a.APPROVAL_ACT_ID, c.STARTER_FORM_ID, h.FORM_KEY 
FROM    clos_dev.approval_activity_log a, clos_dev.obligor_information b, clos_dev.wf_activity_setup c, clos_dev.wf_user_package_approval_log d, clos_dev.wf_user_package_chain_log e,
		clos_dev.branch f, clos_dev.operational_business_segment g, clos_dev.module_user_form h
WHERE   a.OBS_ID = b.OBS_ID
AND     a.OBLIGOR_ID = b.OBLIGOR_ID
AND     a.CURRENT_ACTIVITY_ID = c.Activity_ID 
AND     d.LOG_ID = e.LOG_ID
AND     d.APPROVAL_ACT_ID = e.APPROVAL_ACT_ID
AND     a.APPROVAL_ACT_ID = d.APPROVAL_ACT_ID
AND     e.CURRENT_AT = 1
AND     a.CURRENT_ACTIVITY_ID >= 1
AND     b.BRANCH_ID = f.BRANCH_ID
AND     b.OBS_ID = g.OBS_ID
AND     e.ASSIGNED_EMP_ID = ASSIGNED_EMP_ID
AND 	h.FORM_ID = c.STARTER_FORM_ID
UNION
select b.OBLIGOR_CODE'RequestCode', b.OBLIGOR_CODE, b.OBLIGOR_ID, b.OBLIGOR_NAME 'CustomerName', DATE_FORMAT(a.CREATED_DATE,'%d %b %Y')'CREATEDDATE', a.CREATED_BY 'CREATEDBY', c.SHORT_DESC 'RequestStatus',
c.ACTIVITY_NAME as 'RequestType', f.BRANCH_NAME, g.OBS_NAME, b.CIF,a.APPROVAL_ACT_ID, c.STARTER_FORM_ID, h.FORM_KEY 
FROM   clos_dev.approval_activity_log a, clos_dev.obligor_information b, clos_dev.wf_activity_setup c, clos_dev.wf_user_package_approval_log d,
	   clos_dev.branch f, clos_dev.operational_business_segment g, clos_dev.module_user_form h
WHERE 	a.OBS_ID = b.OBS_ID
AND   	a.OBLIGOR_ID = b.OBLIGOR_ID
AND   	a.CURRENT_ACTIVITY_ID = c.ACTIVITY_ID 
AND   	a.APPROVAL_ACT_ID = d.APPROVAL_ACT_ID
AND   	d.WF_ACTION_ID is null
AND   	a.CURRENT_ACTIVITY_ID = c.ACTIVITY_ID
AND     b.BRANCH_ID = f.BRANCH_ID
AND     b.OBS_ID = g.OBS_ID
AND   	d.E_ID = ASSIGNED_EMP_ID
AND 	h.FORM_ID = c.STARTER_FORM_ID
UNION
SELECT  b.OBLIGOR_CODE'RequestCode', b.OBLIGOR_CODE, b.OBLIGOR_ID, b.OBLIGOR_NAME 'CustomerName',DATE_FORMAT(b.CREATED_DATE,'%d %b %Y') 'CREATEDDATE', b.CREATED_BY 'CREATEDBY',
'Initiate' as 'RequestStatus', @activity_name as 'RequestType', f.BRANCH_NAME, g.OBS_NAME, b.CIF, -1 APPROVAL_ACT_ID, 3, h.FORM_KEY
FROM    obligor_information b,  clos_dev.branch f, clos_dev.operational_business_segment g, clos_dev.module_user_form h
WHERE   b.created_by = (select login_id from employee_credentials where e_id = ASSIGNED_EMP_ID and is_resign = 0)
AND		b.OBLIGOR_NAME is not null
AND     b.BRANCH_ID = f.BRANCH_ID
AND     b.OBS_ID = g.OBS_ID
AND 	h.FORM_ID = 3
AND NOT EXISTS (SELECT * FROM approval_activity_log WHERE OBLIGOR_ID = b.OBLIGOR_ID)
UNION 
SELECT  b.OBLIGOR_CODE'RequestCode', b.OBLIGOR_CODE, b.OBLIGOR_ID, b.OBLIGOR_NAME 'CustomerName', DATE_FORMAT(a.CREATED_DATE,'%d %b %Y')'CREATEDDATE', a.CREATED_BY 'CREATEDBY', c.SHORT_DESC 'RequestStatus',
c.ACTIVITY_NAME as 'RequestType', f.BRANCH_NAME, g.OBS_NAME, b.CIF,a.APPROVAL_ACT_ID, c.STARTER_FORM_ID, h.FORM_KEY
FROM    clos_dev.approval_activity_log a, clos_dev.obligor_information b, clos_dev.wf_activity_setup c, 
		clos_dev.branch f, clos_dev.operational_business_segment g, clos_dev.module_user_form h
WHERE   a.OBS_ID = b.OBS_ID
AND     a.OBLIGOR_ID = b.OBLIGOR_ID
AND     a.CURRENT_ACTIVITY_ID = c.activity_id 
AND     b.BRANCH_ID = f.BRANCH_ID
AND     b.OBS_ID = g.OBS_ID
and     c.activity_id  in (select activity_id from wf_activity_setup where activity_key = concat(left(Activity_Key,2) , 'A'))
AND     a.RM_CODE = ASSIGNED_EMP_ID
AND 	h.FORM_ID = c.STARTER_FORM_ID;
END ;;
