CREATE DEFINER=`root`@`%` PROCEDURE `get_chainLOG`(IN APPROVAL_ACT_ID INT )
BEGIN
  SELECT ( select CONCAT(x.FIRST_NAME, " ", x.LAST_NAME) from  employee_credentials  x where x.E_ID = a.E_ID  ) as 'ForwaredBy' ,
		 ( select  CONCAT(x.FIRST_NAME, " ", x.LAST_NAME) from  employee_credentials x where x.E_ID = a.ASSIGNED_EMP_ID ) as 'ForwaredTo',
         (select x.OnscreenName from wf_userprofile_config x where x.WF_PROFILE_ID = a.NEW_WF_PROFILE_ID )'ForwardedByProfile' ,
		 (select x.OnscreenName from wf_userprofile_config  x where WF_PROFILE_ID = (select y.NEW_WF_PROFILE_ID from wf_user_package_approval_log y 
         where  y.SEQUENCE = 2 and  y.APPROVAL_ACT_ID = APPROVAL_ACT_ID   ))'ForwardedToProfile' ,
         a.COMMENT ,a.OTHER_REMARKS ,  
         a.ACTION_DATE,
         a.*
        from wf_user_package_approval_log a 
        where a.WF_ACTION_ID is not null
        and   a.SEQUENCE =1
        and   a.APPROVAL_ACT_ID = APPROVAL_ACT_ID
 UNION
  SELECT ( select CONCAT(x.FIRST_NAME, " ", x.LAST_NAME) from  employee_credentials x where x.E_ID = a.E_ID  ) as 'ForwaredBy' ,
		 ( select  CONCAT(x.FIRST_NAME, " ", x.LAST_NAME) from  employee_credentials x where x.E_ID = a.ASSIGNED_EMP_ID ) as 'ForwaredTo',
         (select x.OnscreenName from wf_userprofile_config x where x.WF_PROFILE_ID = a.NEW_WF_PROFILE_ID )'ForwardedByProfile' ,
		 (select x.OnscreenName from wf_userprofile_config x where x.WF_PROFILE_ID = (select y.NEW_WF_PROFILE_ID from wf_user_package_approval_log  y where  y.SEQUENCE = a.SEQUENCE+1  and  y.APPROVAL_ACT_ID = APPROVAL_ACT_ID))'ForwardedToProfile' ,
         a.COMMENT ,a.OTHER_REMARKS , a.ACTION_DATE , a.*
        from wf_user_package_approval_log a         
        where a.WF_ACTION_ID is not null
        and   a.SEQUENCE != 1
        and   a.APPROVAL_ACT_ID = APPROVAL_ACT_ID
        order by SEQUENCE desc ;   
END ;;
