CREATE DEFINER=`root`@`%` PROCEDURE `get_tprsdocuments`(IN OBLIGOR int  )
BEGIN
select distinct a.DOC_ID,concat(c.FACILITY_DESC,'-',a.DOC_NAME) 'DOC_NAME'
from doc_checklist a, doc_checklist_facility b , facility c , obligor_information d  , customer_facility f -- , doc_checklist_obs e 
where a.DOC_ID = b.DOC_ID
and b.FACILITY_ID = c.FACILITY_ID 
-- and d.OBS_ID = e.OBS_ID
-- and e.DOC_ID = a.DOC_ID 
and d.OBLIGOR_ID = OBLIGOR
and a.STATUS_ID = 1 
and d.OBLIGOR_ID = f.OBLIGOR_ID
and f.FACILITY_ID = c.FACILITY_ID
 UNION
select distinct a.DOC_ID,concat(c.COLLATERAL_DESC,'-',a.DOC_NAME) 'DOC_NAME'
from doc_checklist a, doc_checklist_collateral b , collateral_stp c , obligor_information d  ,cf_collateral f -- , doc_checklist_obs e
where a.DOC_ID = b.DOC_ID
and b.COLLATERAL_ID = c.COLLATERAL_STP_ID 
-- and d.OBS_ID = e.OBS_ID
-- and e.DOC_ID = a.DOC_ID 
and d.OBLIGOR_ID = OBLIGOR
and a.STATUS_ID = 1 
and d.OBLIGOR_ID = f.OBLIGOR_ID
and f.COLLATERAL_STP_ID = c.COLLATERAL_STP_ID;
end ;;
