CREATE DEFINER=`root`@`%` PROCEDURE `get_tprs_linkage`(IN OBLIGOR INT, ACTIVITY_ID INT )
BEGIN
select a.CF_ID,b.CF_LINK_ID ,a.OBLIGOR_ID,b.APPROVAL_ACT_ID,a.FACILITY_ID , (select FACILITY_DESC from facility where FACILITY_ID = a.FACILITY_ID)'FACILITY_DESC' , 
 (Select COLLATERAL_DESC from collateral_stp where COLLATERAL_STP_ID = b.CF_COLLATERAL_ID) 'COLLATERAL_DESC'
 ,a.FAC_AS_ID,a.MAIN_FACILITY_ID,a.ROOT, (case when a.SEQUENCE is null then a.root else a.SEQUENCE end )'SEQUENCE'   , b.IS_LINK, 
 b.CF_COLLATERAL_ID 
