where a.OBLIGOR_ID = OBLIGOR 
and  a.APPROVAL_ACT_ID =ACTIVITY_ID
and a.OBLIGOR_ID = b.OBLIGOR_ID
and a.APPROVAL_ACT_ID = b.APPROVAL_ACT_ID
and a.CF_ID = b.CF_ID 
order by SEQUENCE;
