CREATE DEFINER=`root`@`%` PROCEDURE `IOMmemo_facilityCompliance`(IN OBLIGOR int )
BEGIN
SET @row := 0;
select @row := @row + 1 as row1, concat(c.FACILITY_NAME,"- PKR -",a.FACILITY_REQUESTED_AMOUNT) as 'Facility',
e.STATUS_DESC as 'Status',
IFNULL((SELECT ComplianceDate FROM  IOM_compliancestatus  WHERE CF_ID = A.CF_ID LIMIT 1  ),'') 'ComplianceDate',
