and a.APPROVAL_ACT_ID = b.APPROVAL_ACT_ID

and c.FACILITY_TYPE_ID = d.FACILITY_TYPE_ID 
and a.FACILITY_ID = c.FACILITY_ID 
and a.FAC_AS_ID = e.FAC_AS_ID ; 

end ;;
