CREATE DEFINER=`root`@`%` PROCEDURE `sp_BCA_FacilitiesRpt`(IN OBG_ID INT(11))
BEGIN
 SELECT IFNULL(CF.sequence,CF.root) Sequence,fac.FACILITY_NAME, Format(cf.LAST_APPROVED_LIMIT,0) LAST_APPROVED_LIMIT, Format(cf.FACILITY_REQUESTED_AMOUNT,0) FACILITY_REQUESTED_AMOUNT, 
 case when (cf.FACILITY_REQUESTED_AMOUNT > cf.LAST_APPROVED_LIMIT) then Format(cf.FACILITY_REQUESTED_AMOUNT - cf.LAST_APPROVED_LIMIT,0) else 
 concat('(',Format(cf.LAST_APPROVED_LIMIT - cf.FACILITY_REQUESTED_AMOUNT,0) ,')') end as IncDec, Format(cf.OUTSTANDING_PRINCIPLE,0) OUTSTANDING_PRINCIPLE,
 ft.FAC_TENURE_NAME, DATE_FORMAT(cf.FACILITY_MATURITY_DATE,'%d %b %Y') FACILITY_MATURITY_DATE,DATE_FORMAT(cf.FACILITY_EXPIRY_DATE,'%d %b %Y')FACILITY_EXPIRY_DATE,Format(CF.OVERDUE_PRINCIPLE,0) OVERDUE_PRINCIPLE,
 (CASE WHEN facType.FACILITY_TYPE_DESC_ID = 'F'  THEN 'FUNDED' WHEN facType.FACILITY_TYPE_DESC_ID = 'N'  THEN 'NON-FUNDED' ELSE 'N/A' END) FACILITYTYPE,
 CONCAT('Commission(%) :', cfr.LG_COMMISSION, ' || ', 'Investment Bank Fees: ',cfr.BANK_FEES, ' || ','Retirement Charges: ',cfr.RETIREMENT_CHARGES_PROPOSED,' || ',
  'Advising Charges: ',cfr.ADVISING_CHARGES) as MARKUP_EXISTING_F,
  CONCAT('Commission(%) :', cfr.LG_COMMISSION_PROPOSED, ' || ', 'Investment Bank Fees: ',cfr.BANK_FEES_PROPOSED, ' || ','Retirement Charges: ',cfr.RETIREMENT_CHARGES,' || ',
  'Advising Charges: ',cfr.ADVISING_CHARGES_PROPOSED) as MARKUP_PROPOSED_F,
   CONCAT('Benchmark Rates : ', br.BENCHMARK_NAME, ' || ', 'Spread(%): ',cfr.SPREAD_EXISTING) as MARKUP_EXISTING_NF,
 CONCAT('Benchmark Rates : ', br.BENCHMARK_NAME, ' || ', 'Spread(%): ',cfr.SPREAD) as MARKUP_PROPOSED_NF,
 CASE when cf.MAIN_FACILITY_ID IS NULL THEN 'Main' else 'Sub' end  LimitType
  from clos_dev.customer_facility cf
 inner join clos_dev.facility fac on  fac.FACILITY_ID =cf.FACILITY_ID
 left  join  clos_dev.facility_tenure ft on ft.FAC_TENURE_ID= cf.FAC_TENURE_ID
 left  join  clos_dev.customer_facility_repayment cfr on cfr.CF_ID=cf.CF_ID
 left  join  clos_dev.facility_type facType on facType.FACILITY_TYPE_ID = fac.FACILITY_TYPE_ID
 left  join  clos_dev.benchmark_rates br on br.BENCHMARK_ID=cfr.BENCHMARK_ID
 left  join  clos_dev.benchmark_rates brex on brex.BENCHMARK_ID=cfr.BENCHMARK_ID_EXISTING
  where  cf.OBLIGOR_ID=OBG_ID and cf.IS_DELETED=0  group by  fac.FACILITY_NAME ;
END ;;
