CREATE DEFINER=`root`@`%` PROCEDURE `sp_BCA_GroupDetail`(IN Obligor_ID int)
BEGIN 
SELECT (@row:=@row+1) AS 'No',A.OBLIGOR_NAME COMPANIES,Format(x.funded,0) PROPOSED_FUNDED_LIMIT,Format(x.nonfunded,0) PROPOSED_NONFUNDED_LIMIT, 
Format(x.overall,0)PROPOSED_TOTAL, format(x.ex_funded,0) EXISTING_FUNDED_LIMIT, Format(x.ex_nonfunded,0) EXISTING_NONFUNDED_LIMIT,
Format(x.ex_overall,0) EXISTING_TOTAL,
case when (ifnull(x.ex_funded,0) + ifnull(x.ex_nonfunded,0)) > (ifnull(x.funded,0) + ifnull(x.nonfunded,0))
then 
Concat('(',Format((ifnull(x.ex_funded,0) + ifnull(x.ex_nonfunded,0)) - (ifnull(x.funded,0) + ifnull(x.nonfunded,0)) ,0),')')
else 
Format((ifnull(x.funded,0) + ifnull(x.nonfunded,0)) - (ifnull(x.ex_funded,0) + ifnull(x.ex_nonfunded,0)),0) end 'Change' 
 ,Format(x.Outstanding,0) Outstanding,
 DATE_FORMAT(c.current_expiry,'%d %b %Y') ExpiryDate, g_orr.group_orr_value ORR,x.approval_act_id
 FROM approval_limits_sum x 
 inner join obligor_information a on a.obligor_id = x.obligor_id
inner join group_basic_detail b ON b.GROUP_BD_ID = a.OBLIGOR_GROUP_ID
inner join approval_activity_log c on  c.approval_act_id = (select y.approval_act_id from approval_activity_log y where y.obligor_id = x.obligor_id  order by y.approval_act_id desc limit 1)
left join clos_dev.orr orr on orr.obligor_id = a.obligor_id and orr.approval_act_id = x.approval_act_id
left join clos_dev.group_orr g_orr on g_orr.group_orr_key = ifNull(orr.risk_orr_model,orr.orr_model)
,(SELECT @row := 0) r
WHERE a.OBLIGOR_GROUP_ID = (select x.OBLIGOR_GROUP_ID from obligor_information x where x.obligor_id = Obligor_ID limit 1);

END ;;
