CREATE DEFINER=`root`@`%` PROCEDURE `sp_CloneData`(IN APPROVAL_ID int(11))
BEGIN
	DECLARE msg varchar(2000) default "0";
	DECLARE APPROVED_APPR_ID int(11) default 0;
    DECLARE qryStr varchar(6000) default null;
    DECLARE finished INTEGER DEFAULT 0;
    DECLARE T_NAME varchar(100);
    DECLARE W_COLUMN varchar(100);
    declare COL_NAME varchar(5000);
	DEClARE curCloneData
		CURSOR FOR 
			SELECT TBL_NAME,WHERE_COLUMN FROM action_query where status = 'A' order by sequence;

	-- declare NOT FOUND handler
	DECLARE CONTINUE HANDLER 
        FOR NOT FOUND SET finished = 1;
        
    DECLARE EXIT HANDLER FOR SQLEXCEPTION 
    BEGIN 
        GET DIAGNOSTICS CONDITION 1 @p1 = RETURNED_SQLSTATE, @p2 = MESSAGE_TEXT;
        set msg = CONCAT(@p1, ':', @p2);
		SELECT concat(msg,' Table Name : ',T_NAME) ;
    END;
    SET @OBG_ID = (select OBLIGOR_ID from approval_activity_log where APPROVAL_ACT_ID = APPROVAL_ID limit 1);
    SET APPROVED_APPR_ID = (select a.APPROVAL_ACT_ID from approval_activity_log a 
							 inner join wf_activity_setup b on a.CURRENT_ACTIVITY_ID = b.Activity_ID 
							   where a.OBLIGOR_ID = @OBG_ID and a.ACT_STATUS = 'A' and b.`Activity Type` = 'AP'
                                ORDER BY a.APPROVAL_ACT_ID DESC LIMIT 1 );
                             --   select * from approval_activity_log  where OBLIGOR_ID = 244;
	OPEN curCloneData;
	CloneData: LOOP
		FETCH curCloneData INTO T_NAME,W_COLUMN;
		IF finished = 1 THEN 
			LEAVE CloneData;
		END IF;
		SET COL_NAME = (select get_table_columns(T_NAME,'clos_dev'));
		 SET qryStr = concat('insert into ',T_NAME,'(',COL_NAME,')',' select ',REPLACE(COL_NAME,W_COLUMN,APPROVAL_ID),' from ',T_NAME, ' where ',W_COLUMN,' = ',APPROVED_APPR_ID ,';');
        	set @data = (qryStr); 
            -- select @data;
		 	PREPARE query from @data; 
	  	 EXECUTE query ;
	   	 deallocate prepare query;
        
    END LOOP CloneData;
	CLOSE curCloneData;
		-- set @data = (qryStr); 
	 -- 	 select @data;
	   --   PREPARE query from @data; 
	 	-- EXECUTE query ;
	  	-- deallocate prepare query;
END ;;
