CREATE DEFINER=`root`@`%` PROCEDURE `sp_GetAuditData`(IN Form_Name varchar(100),IN Tbl_Name varchar(100),IN Act char(1),IN PKValue int(11))
BEGIN
	DECLARE msg varchar(2000) default "0";
	DECLARE Frm_ID int(11) default 0;
    DECLARE qryStr varchar(6000) default "0";
    
    DECLARE EXIT HANDLER FOR SQLEXCEPTION 
    BEGIN 
        GET DIAGNOSTICS CONDITION 1 @p1 = RETURNED_SQLSTATE, @p2 = MESSAGE_TEXT;
        set msg = CONCAT(@p1, ':', @p2);
		SELECT msg;
    END;
    
    SET Frm_ID = (select FORM_ID from module_user_form where FORM_KEY = Form_Name Limit 1);
    SET qryStr = ifnull(( select QRY from audit_config where FORM_ID = Frm_ID and ACTION = Act and PRIMARY_TABLE_NAME = Tbl_Name Limit 1),"0");
   
    IF (ifnull(Frm_ID,0) = 0 ) then 
    BEGIN
      SET msg = 'Form Not Exists '; SELECT msg; 
    END;
    ELSEIF (qryStr != "0") THEN
    BEGIN
        SET qryStr = REPLACE(qryStr,'@CODE',PKValue);
        -- execute qryStr;
		set @data = (qryStr); 
		-- select @data;
		 PREPARE query from @data; 
		 EXECUTE query ;
		 deallocate prepare query;
    END;
    ELSE 
	BEGIN
		SET msg = 'No record found in Audit Config  !';
		SELECT msg;
	END;
END IF;
END ;;
