CREATE DEFINER=`root`@`%` PROCEDURE `sp_getEmployeeRights`(IN E_ID INT(11))
BEGIN
 select ifnull(emp_profile_id,0) PKID,a.user_profile_id CODE,user_profile_name NAME,ifnull(b.STATUS,'I') STATUS,CONV(ifnull(b.IS_DEFAULT,0), 16, 2) IS_DEFAULT ,'P' TYP
from user_profile a
left join employee_profile b on a.user_profile_id = b.user_profile_id and b.e_id = E_ID
where a.is_deleted = 0
UNION
select ifnull(emp_business_id,0) PKID ,a.obs_id CODE,obs_name NAME,ifnull(b.STATUS,'I')  STATUS,0 IS_DEFAULT ,'S' TYP
from operational_business_segment a
left join employee_business_hierarchy b on b.bh_id = a.obs_id and b.e_id = E_ID
where a.is_deleted = 0
UNION
select ifnull(emp_branch_id,0) PKID ,a.branch_id CODE,branch_name NAME,ifnull(b.STATUS,'I')  STATUS ,0 IS_DEFAULT ,'B' TYP 
from branch a
left join employee_branch b on b.branch_id = a.branch_id and b.e_id = E_ID
where a.is_deleted = 0
ORDER BY TYP,CODE ;
END ;;
