CREATE DEFINER=`root`@`%` PROCEDURE `sp_getFacilitiesForRpt`(IN OBG_ID INT(11))
BEGIN
 SELECT IFNULL(CF.sequence,CF.root) Sequence,fac.FACILITY_NAME, cf.LAST_APPROVED_LIMIT, cf.FACILITY_REQUESTED_AMOUNT, cf.OUTSTANDING_PRINCIPLE,
 ft.FAC_TENURE_NAME, DATE_FORMAT(cf.FACILITY_MATURITY_DATE,'%d %b %Y') FACILITY_MATURITY_DATE,DATE_FORMAT(cf.FACILITY_EXPIRY_DATE,'%d %b %Y')FACILITY_EXPIRY_DATE,CF.OVERDUE_PRINCIPLE,
GROUP_CONCAT(CONCAT(cols.COLLATERAL_NAME," ",
 " Type:",CT.COLLATERAL_TYPE_NAME," ",
 " Market Value:",cfcol.MARKET_VALUE," " ,
 " Force Sales Value:",cfcol.FORCE_SALES_VALUE , " " ,
 " Primary Or Secondary:",(CASE WHEN cfl.PRIMARY_SECONDARY = 0  THEN 'Primary ' ELSE 'Secondary ' END),
 " Valuation Date:",cfcol.VALUATION_DATE," " ,
 " Valuator Name:",cfcol.VALUATED_BY, " " ,
 " Charge:",ac.AVG_SC_NAME, " " ,
 " Legal Counsil:",lc.LEGAL_COUNCIL_DESC, " "
 " Legal Opinion Date:", DATE_FORMAT(legal_op_date,'%d %b %Y') ," ",
 (CASE WHEN cfcol.CLEAN_UP = 1  THEN ' Clean Up:Yes ' ELSE '' END), 
 (CASE WHEN cfcol.property_docs = 1  THEN ' Property Documents:Yes ' ELSE '' END) , 
 (CASE WHEN cfcol.Collateral_Marketability = 1  THEN ' Collateral Marketability:Yes ' ELSE '' END) , 
 (CASE WHEN cfcol.Collateral_Salability = 1  THEN ' Collateral Salability:Yes ' ELSE '' END) , 
 (CASE WHEN cfcol.Collateral_Accessibility = 1  THEN ' Collateral Accessibility:Yes ' ELSE '' END)
 ) SEPARATOR ' || ')
 as ASSOCIATED_COLLATERAL, (CASE WHEN facType.FACILITY_TYPE_DESC_ID = 'F'  THEN 'FUNDED' WHEN facType.FACILITY_TYPE_DESC_ID = 'N'  THEN 'NON-FUNDED' ELSE 'N/A' END) FACILITYTYPE,
 CONCAT('Commission(%) :', cfr.LG_COMMISSION, ' || ', 'Investment Bank Fees: ',cfr.BANK_FEES, ' || ','Retirement Charges: ',cfr.RETIREMENT_CHARGES_PROPOSED,' || ',
  'Advising Charges: ',cfr.ADVISING_CHARGES) as MARKUP_EXISTING_F,
  CONCAT('Commission(%) :', cfr.LG_COMMISSION_PROPOSED, ' || ', 'Investment Bank Fees: ',cfr.BANK_FEES_PROPOSED, ' || ','Retirement Charges: ',cfr.RETIREMENT_CHARGES,' || ',
  'Advising Charges: ',cfr.ADVISING_CHARGES_PROPOSED) as MARKUP_PROPOSED_F,
   CONCAT('Benchmark Rates : ', br.BENCHMARK_NAME, ' || ', 'Spread(%): ',cfr.SPREAD_EXISTING) as MARKUP_EXISTING_NF,
 CONCAT('Benchmark Rates : ', br.BENCHMARK_NAME, ' || ', 'Spread(%): ',cfr.SPREAD) as MARKUP_PROPOSED_NF,
 CASE when cf.MAIN_FACILITY_ID IS NULL THEN 'Main' else 'Sub' end  LimitType
  from clos_dev.customer_facility cf
 inner join clos_dev.cf_collateral_linkage cfl on cfl.CF_ID =cf.CF_ID and cfl.obligor_id = cf.obligor_id
 inner join clos_dev.cf_collateral cfcol on cfl.CF_COLLATERAL_ID=cfcol.CF_COLLATERAL_ID and cfcol.obligor_id = cfl.obligor_id
 inner join clos_dev.collateral_stp cols on cols.COLLATERAL_STP_ID= cfcol.COLLATERAL_STP_ID
 inner join clos_dev.facility fac on  fac.FACILITY_ID =cf.FACILITY_ID
 left  join  clos_dev.facility_tenure ft on ft.FAC_TENURE_ID= cf.FAC_TENURE_ID
 left  join  clos_dev.customer_facility_repayment cfr on cfr.CF_ID=cf.CF_ID
 left  join  clos_dev.facility_type facType on facType.FACILITY_TYPE_ID = fac.FACILITY_TYPE_ID
 left  join  clos_dev.benchmark_rates br on br.BENCHMARK_ID=cfr.BENCHMARK_ID
 left  join  clos_dev.benchmark_rates brex on brex.BENCHMARK_ID=cfr.BENCHMARK_ID_EXISTING
 left  join  clos_dev.average_seniority_charge ac on ac.avg_sc_id = cfcol.avg_sc_id
 left  JOIN  clos_dev.collateral_type ct on ct.COLLATERAL_TYPE_ID = cfcol.COLLATERAL_TYPE_ID
 left  Join  clos_dev.legal_council lc on lc.legal_council_id= cfcol.legal_cons_id
  where  cf.OBLIGOR_ID=OBG_ID and  cfl.IS_LINK=1 and cf.IS_DELETED=0  group by  fac.FACILITY_NAME ;
END ;;
