CREATE DEFINER=`root`@`%` PROCEDURE `sp_getFinancials`(IN OBG_ID INT(11))
BEGIN
IF (OBG_ID = 0) THEN 
BEGIN 
SELECT a.COA_ID,a.COA_NAME,a.FIELD_TYPE,b.FORMULA_EXPRESSION,b.RATIO_EXPRESSION, IFNULL(d.coa_value,0) as COA_VALUE ,
		IFNULL(d.coa_ratio,0) as COA_RATIO , a.COA_SEQUENCE,c.IS_AUDITED, CONCAT(c.AUDITOR_ID,":",e.AUDITOR_NAME,""," (",e.AUDITOR_RATING,")") AUDITOR_ID,
		c.FINANCIAL_YEAR,c.FREQUENCY_ID,c.START_DATE,c.END_DATE,c.FINANCIAL_TYPE,
		IFNULL(d.CFD_ID,0) as CFD_ID,IFNULL(c.CFS_ID,0) as CFS_ID
FROM chart_of_accounts_setup a
LEFT JOIN financial_formula_setup b on a.COA_ID = b.COA_ID and b.is_deleted= 0
LEFT JOIN customer_financials c on c.OBLIGOR_ID = OBG_ID and c.IS_DELETED = 0 
LEFT JOIN customer_financials_detail d on c.CFS_ID = d.CFS_ID  and a.COA_ID = d.COA_ID
LEFT JOIN auditor_stp e on e.AUDITOR_ID = c.AUDITOR_ID AND e.IS_DELETED = 0
WHERE a.IS_PEER_COMPANY = 0 and a.IS_DELETED = 0
ORDER BY   c.FINANCIAL_YEAR,cast(ifnull(a.coa_sequence,'0') AS SIGNED);
END;
ELSE 
BEGIN 
SELECT a.COA_ID,a.COA_NAME,a.FIELD_TYPE,b.FORMULA_EXPRESSION,b.RATIO_EXPRESSION, IFNULL(d.coa_value,0) as COA_VALUE ,
IFNULL(d.coa_ratio,0) as COA_RATIO , a.COA_SEQUENCE,c.IS_AUDITED,CONCAT(c.AUDITOR_ID,":",e.AUDITOR_NAME,""," (",e.AUDITOR_RATING,")") AUDITOR_ID,c.FINANCIAL_YEAR,c.FREQUENCY_ID,c.START_DATE,c.END_DATE,c.FINANCIAL_TYPE,
IFNULL(d.CFD_ID,0) as CFD_ID,IFNULL(c.CFS_ID,0) as CFS_ID
FROM chart_of_accounts_setup a
LEFT JOIN financial_formula_setup b on a.COA_ID = b.COA_ID and b.is_deleted= 0
INNER JOIN customer_financials c on c.OBLIGOR_ID = OBG_ID and c.is_deleted = 0
LEFT JOIN  customer_financials_detail d on c.CFS_ID = d.CFS_ID  and a.COA_ID = d.COA_ID
LEFT JOIN auditor_stp e on e.AUDITOR_ID = c.AUDITOR_ID AND e.IS_DELETED = 0
WHERE a.IS_PEER_COMPANY = 0 and a.IS_DELETED = 0
ORDER BY  c.FINANCIAL_YEAR,cast(ifnull(a.coa_sequence,'0') AS SIGNED) asc;
END;
END IF;
END ;;
