CREATE DEFINER=`root`@`%` PROCEDURE `sp_GetTemplate`(IN ACTION_ID INT,IN APPR_ACT_ID INT,IN FROM_EMP INT,IN TO_EMP INT)
BEGIN
declare Template mediumtext default '' ;
declare ActionTaken char(5) default '';
   DECLARE EXIT HANDLER FOR SQLEXCEPTION 
    BEGIN 
        GET DIAGNOSTICS CONDITION 1 @p1 = RETURNED_SQLSTATE, @p2 = MESSAGE_TEXT;
        -- set msg = CONCAT(@p1, ':', @p2);
		SELECT CONCAT(@p1, ':', @p2) FROM_EMAIL,'' EMAIL_FORMAT,'' TO_EMAIL;
    END;
 -- 'set Template  = (SELECT IFNULL(( SELECT b.template_message FROM wf_decidingactions a\n					 inner join email_template b on a.email_template = b.template_id \n					 where wf_action_id = ACTION_ID),\'\'));'

SELECT b.template_message,a.action_taken 
 into Template,ActionTaken 
FROM wf_decidingactions a
					 inner join email_template b on a.email_template = b.template_id 
					 where wf_action_id = ACTION_ID Limit 1 ;
                     
 IF (Template != '' && (select count(*) from employee_credentials where e_id = FROM_EMP) > 0 and 
 (select count(*) from employee_credentials where e_id = TO_EMP) > 0 or (ActionTaken = 'A')) THEN  
 BEGIN       
 
 declare From_Emp_Name  varchar(100) default null;
 declare From_Emp_Profile varchar(100) default null;
 declare From_Emp_Email varchar(100) default null;
 declare To_Emp_Name  varchar(100) default null;
 declare To_Emp_Profile varchar(100) default null;
 declare To_Emp_Email varchar(100) default null;
  
 declare OBG_NAME  varchar(100) default null;
 declare SEG  varchar(100) default null;
 declare CREATED_BY  varchar(100) default null;
 declare BRANCH  varchar(100) default null;
 declare ACTIVITY_NAME  varchar(100) default null;
 declare STATE  varchar(100) default null;
 declare BANK  varchar(100) default null;
 
 SELECT CONCAT(IFNULL(FIRST_NAME,''), " ", IFNULL(LAST_NAME,'')) , c.user_profile_name,a.email
  into From_Emp_Name, From_Emp_Profile  ,From_Emp_Email
        FROM employee_credentials a 
        inner join employee_profile b on a.e_id = b.e_id and b.is_default = 0 
        inner join user_profile c on c.user_profile_id = b.user_profile_id 
        where a.E_ID = FROM_EMP limit 1  ;
        
 SELECT CONCAT(IFNULL(FIRST_NAME,''), " ", IFNULL(LAST_NAME,'')) , c.user_profile_name,a.email
  into To_Emp_Name , To_Emp_Profile , To_Emp_Email 
        FROM employee_credentials a 
        inner join employee_profile b on a.e_id = b.e_id and b.is_default = 0 
        inner join user_profile c on c.user_profile_id = b.user_profile_id 
        where a.E_ID = TO_EMP limit 1 ; 

 select b.obligor_name, c.obs_name,CONCAT(IFNULL(f.FIRST_NAME,''), " ", IFNULL(f.LAST_NAME,'')),d.branch_name,e.activity_name,case when e.`activity type` = 'AP' then 'Package' else 'Transaction' end ,
 (select client_name from client where is_active = 1)
 into 	 OBG_NAME,	 SEG,	 CREATED_BY ,	 BRANCH ,	 ACTIVITY_NAME ,	 STATE	 ,BANK  
 from approval_activity_log a 
 left join obligor_information b on a.obligor_id = b.obligor_id 
 left join operational_business_segment c on c.obs_id = a.obs_id 
 left join branch d on d.branch_id = b.branch_id 
 left join wf_activity_setup e on e.activity_id = a.current_activity_id 
 left join employee_credentials f on a.created_by = f.login_id
 where a.approval_act_id = APPR_ACT_ID limit 1 ;
 
   set Template = REPLACE(Template, '<CustomerName>',OBG_NAME);
   set Template = REPLACE(Template, '<Segment>',SEG);
   set Template = REPLACE(Template, "<Createdby>",CREATED_BY);
   set Template = REPLACE(Template, "<Initiator>",CREATED_BY);
   set Template = REPLACE(Template, "<Branch>",BRANCH);
   set Template = REPLACE(Template, "<State>",STATE);
   set Template = REPLACE(Template, "<Type>",ACTIVITY_NAME);
   set Template = REPLACE(Template, "<Sentby>",From_Emp_Name);
   set Template = REPLACE(Template, "<SentEmail>",From_Emp_Email);
   set Template = REPLACE(Template, "<Sentto>",To_Emp_Name);
   set Template = REPLACE(Template, "<DateSentOn>",DATE_FORMAT(now(),'%d %b %Y'));
   set Template = REPLACE(Template, "<Bank>",BANK);
   set Template = REPLACE(Template, "<Profile>",From_Emp_Profile);
   if (ActionTaken = 'A') then 
   begin 
	   select From_Emp_Email FROM_EMAIL,TEMPLATE EMAIL_FORMAT,b.email TO_EMAIL from wf_user_package_approval_log a 
	   inner join employee_credentials b on a.e_id = b.e_id
	   where a.approval_act_id = APPR_ACT_ID
       group by a.e_id,b.email;     
   end ;
   else 
   begin
	   select From_Emp_Email FROM_EMAIL,TEMPLATE EMAIL_FORMAT,To_Emp_Email TO_EMAIL;
   end ;
   end if;
 END;
 ELSE  select '' FROM_EMAIL,'' EMAIL_FORMAT,'' TO_EMAIL;
 END IF;
END ;;
