CREATE DEFINER=`root`@`%` PROCEDURE `sp_get_ext_rating`(IN OTHER_BANK_ID INT)
BEGIN
select IFNULL((select RATING_ID from  other_bank_rating y 
 where y.AGENCY_ID = a.AGENCY_ID and `y`.`OTHER_BANK_ID` = OTHER_BANK_ID limit 1),0) OTHER_BANK_ID,
 AGENCY_ID EXT_RATING_ID,AGENCY_NAME EXT_RATING_NAME,
(select y.LONG_TERM from  other_bank_rating y 
 where y.AGENCY_ID = a.AGENCY_ID and `y`.`OTHER_BANK_ID` = OTHER_BANK_ID limit 1) LONG_TERM,
 (select y.SHORT_TERM from  other_bank_rating y 
 where y.AGENCY_ID = a.AGENCY_ID and `y`.`OTHER_BANK_ID` = OTHER_BANK_ID limit 1) SHORT_TERM,
  (select y.OUTLOOK  from  other_bank_rating y 
 where y.AGENCY_ID = a.AGENCY_ID and `y`.`OTHER_BANK_ID` = OTHER_BANK_ID limit 1) OUTLOOK,
 0 CHECKED,NULL RATING_ID
from credit_rating_agency a where is_deleted = 0
order by AGENCY_ID ;
END ;;
