CREATE DEFINER=`root`@`%` PROCEDURE `sp_get_form_rights`(IN FORM_ID INT(11))
BEGIN
SELECT ifnull(b.id,0) PK,a.obs_id CODE,a.obs_name DESCRIPTION,ifnull(B.IS_ACTIVE,0) CHECKED, 'B' TYP   
FROM  operational_business_segment a 
left join module_forms_dynamic b on a.obs_id = b.obs_id and b.form_id = FORM_ID
left join MODULE_USER_FORM c on c.form_id = b.form_id   and c.form_id = FORM_ID
where a.is_deleted = 0
UNION 
SELECT ifnull(b.id,0) PK,a.user_profile_id CODE,a.USER_PROFILE_NAME DESCRIPTION,ifnull(B.IS_ACTIVE,0)  CHECKED, 'P' TYP   
FROM  user_profile a 
left join module_forms_profile_linkage b on a.user_profile_id = b.user_profile_id and b.form_id = FORM_ID
left join module_user_form c on c.form_id = b.form_id   and c.form_id = FORM_ID
where a.is_deleted = 0
UNION 
SELECT ifnull(b.id,0) PK,a.Activity_ID CODE,concat(a.Activity_Name,' ',a.short_desc) DESCRIPTION,ifnull(B.IS_ACTIVE,0)  CHECKED, 'A' TYP   
FROM  wf_activity_setup a 
left join module_forms_acivity_linkage b on a.Activity_ID = b.Activity_ID and b.form_id = FORM_ID
left join module_user_form c on c.form_id = b.form_id   and c.form_id = FORM_ID
where a.is_deleted = 0 
order by typ,description;
END ;;
