CREATE DEFINER=`root`@`%` PROCEDURE `sp_get_menu`(IN OBG_ID INT(11),IN APPROVAL_ID INT(11),IN USER_CODE INT(11))
BEGIN
	  SET @SEG_ID = (select obs_id from obligor_information where obligor_id = OBG_ID  Limit 1);
      SET @USER_PROFILE = (select user_profile_id from employee_credentials a 
								inner join employee_profile b on a.e_id = b.e_id and b.status  = 'A'
								where a.e_id = USER_CODE limit 1 );
       SET @ACT_ID = (select CURRENT_ACTIVITY_ID from approval_activity_log where approval_act_id = APPROVAL_ID  Limit 1);           
       
       IF (@ACT_ID is null) then 
       begin 
       SELECT * FROM MODULE_USER_FORM where module_user_id = 2 limit 1;
       end;
       else 
       begin 
select a.*
from clos_dev.module_user_form a
inner join clos_dev.module_forms_dynamic b on a.FORM_ID = b.FORM_ID and b.OBS_ID = @SEG_ID and b.IS_ACTIVE=1 
inner join clos_dev.module_forms_profile_linkage c on a.FORM_ID = c.FORM_ID and c.user_profile_id = @USER_PROFILE and c.IS_ACTIVE=1 
inner join clos_dev.module_forms_acivity_linkage d on a.FORM_ID = d.FORM_ID and d.Activity_ID = @ACT_ID and d.IS_ACTIVE=1 
 ORDER BY  b.SEQ_ID;
   end; 
   end if;
   
END ;;
