CREATE DEFINER=`root`@`%` PROCEDURE `sp_per_party_exc_sum`(IN approvalActID int)
BEGIN 
-- SET @TOTAL = (select sum(TOTAL) from obligor_banking_relationship where OBLIGOR_ID = (select OBLIGOR_ID FROM approval_activity_log where APPROVAL_ACT_ID = approvalActID));
SET @RBS_LIMIT = (select cast(round((d.BANK_EQUITY /  a.OVERALL_LIMIT) * 100,2) as decimal(36,2)) EQUITY
                   from  regulatory_limit a 
                   inner join limit_managment d on d.LIMIT_MNG_ID = a.LIMIT_MNG_ID
                   inner join obligor_business_details b on a.rbs_id = b.rbs_id
                   inner join approval_activity_log c on c.obligor_id = b.obligor_id
                    where c.approval_act_id = approvalActID);
 
 SET @GRP_RBS_LIMIT = (select  cast(round((d.BANK_EQUITY / d.OVERALL_LIMIT) * 100,2) as decimal(36,2)) EQUITY from limit_managment d );
                     
 SELECT 
cast(round( SUM(a.OUTSTANDING) ,2) as decimal(36,2)) as EXPOSURE,
cast(round( ifnull(@RBS_LIMIT,0) ,2) as decimal(36,2))   as PER_PARTY_LIMIT,
CASE WHEN cast(round( SUM(a.OUTSTANDING) ,2) as decimal(36,2))  > IFNULL(@RBS_LIMIT,0) THEN 'Complied'
else 'Non-Complied' end as `STATUS`,
'C' as TYPE
   from clos_dev.approval_limits_sum a 
 where APPROVAL_ACT_ID = approvalActID
 
-- select sum(total) from `obligor_banking_relationship` where obligor_id = 211;
 
union all

SELECT 
 cast(round( SUM(a.OUTSTANDING) ,2) as decimal(36,2)) as EXPOSURE,
cast(round( ifnull(@GRP_RBS_LIMIT,0) ,2) as decimal(36,2))   as PER_PARTY_LIMIT,
CASE WHEN cast(round( SUM(a.OUTSTANDING) ,2) as decimal(36,2))  > IFNULL(@GRP_RBS_LIMIT,0) THEN 'Complied'
else 'Non-Complied' end as `STATUS`,
'G' as TYPE
   from clos_dev.approval_activity_log c 
   inner join clos_dev.approval_limits_sum a on a.APPROVAL_ACT_ID = approvalActID
  inner join clos_dev.obligor_information b on a.OBLIGOR_ID = b.OBLIGOR_ID
where b.OBLIGOR_GROUP_ID = (select OBLIGOR_GROUP_ID from clos_dev.obligor_information where OBLIGOR_ID = b.OBLIGOR_ID);
END ;;
