CREATE DEFINER=`root`@`%` PROCEDURE `SP_STARTWF`(IN OBG_ID int(11),IN User_Name varchar(100),IN Expiry_Date datetime,IN Existing_Expiry datetime,IN Annual_Date datetime)
BEGIN
	DECLARE msg varchar(6000) default "0";
	DECLARE Act_ID int(11) default 0;
    DECLARE WF_ID int(11) default 0;
    DECLARE WF_Prof_ID int(11) default 0;
    DECLARE SEG_ID int(11) default 0;
	DECLARE User_Code int(11) default "0";
    
    DECLARE EXIT HANDLER FOR SQLEXCEPTION 
    BEGIN 
        GET DIAGNOSTICS CONDITION 1 @p1 = RETURNED_SQLSTATE, @p2 = MESSAGE_TEXT;
        set msg = CONCAT(@p1, ':', @p2);
		SELECT msg;
    END;
    
    SET Act_ID = (select Activity_ID from wf_activity_setup where is_starter = 1 Limit 1);
    SET WF_ID = ifnull((select b.wf_basic_id from obligor_information a inner join wf_basicelement b on a.obs_id = b.obs_id and b.isdeleted = 0 where obligor_id = OBG_ID Limit 1),0);
    SET WF_Prof_ID = ifnull((select WF_PROFILE_ID from wf_userprofile_config where wf_basic_id = WF_ID and Start_isActive = 1  Limit 1),0);
	SET User_Code = ifnull((select e_id from employee_credentials where login_id = User_Name  Limit 1),0);
    SET SEG_ID = (select obs_id from obligor_information where obligor_id = OBG_ID  Limit 1);
    SET @IFExists = EXISTS(SELECT * FROM approval_activity_log WHERE OBLIGOR_ID = OBG_ID);
    
    IF (ifnull(OBG_ID,0) = 0 ) then 
    BEGIN
      SET msg = 'Obligor ID is null , not Allowed'; SELECT msg; 
    END;

ELSEIF (WF_ID = 0) THEN 
	BEGIN  SET msg = 'Please link workflow with obligor selected business segment before you proceed...'; SELECT msg; 
    END;     
ELSEIF (WF_Prof_ID = 0) THEN 
	BEGIN  SET msg = 'Please add profiles on obligor selected workflow before you proceed...';  SELECT msg; 
    END; 
ELSEIF (User_Code = 0) THEN 
	BEGIN  SET msg = 'Given user name not exists...';  SELECT msg; 
    END; 
ELSEIF (@IFExists = 0) THEN
    BEGIN
-- Insert Statement 
	INSERT INTO approval_activity_log(OBLIGOR_ID, EXISTING_EXPIRY_DATE, LAST_ANNUAL_DATE, CURRENT_EXPIRY, OBS_ID, ACTIVITY_ID, CURRENT_ACTIVITY_ID, WF_BASIC_ID, WF_PROFILE_ID, RM_CODE, ACT_STATUS, CREATED_BY, CREATED_DATE, IS_DELETED,LAST_ACT_DATE)
	VALUES (OBG_ID,Existing_Expiry,Annual_Date,Expiry_Date,SEG_ID,Act_ID,Act_ID,WF_ID,WF_Prof_ID,User_Code,'I',User_Name,NOW(),0,NOW());
	SET @APPROVAL_ACT_ID = LAST_INSERT_ID();
    
--  SET KEY
     UPDATE APPROVAL_ACTIVITY_LOG SET APPROVAL_CODE = concat('APR-',CAST(@APPROVAL_ACT_ID AS char (9))) WHERE APPROVAL_ACT_ID = @APPROVAL_ACT_ID; 
 
 -- WF Start Statement   
	INSERT INTO wf_user_package_approval_log (NEW_WF_PROFILE_ID,ACTION_DATE, ACTION_TAKEN, SEQUENCE, E_ID, APPROVAL_ACT_ID)
	VALUES (WF_Prof_ID,NOW(),'S','1',User_Code,@APPROVAL_ACT_ID);
    SET @LOG_ID = LAST_INSERT_ID();
    
    SET msg = 'Success';
	SELECT msg;
    END;
ELSE 
	BEGIN
		SET msg = 'Package Already Exists !';
		SELECT msg;
	END;
END IF;
END ;;
