             BEGIN
             insert into module_forms_dynamic (FORM_ID, OBS_ID, IS_ACTIVE)values (FORM_ID,CODE,CHECKED);
             SELECT 'Success' msg;
             END;
			ELSE
	         BEGIN  
             update  module_forms_dynamic set IS_ACTIVE = CHECKED where ID = PK;
             SELECT 'Success' msg;
             END;  
			 END IF;
 END;
 ELSEIF (TYP = 'A') then 
    BEGIN
            IF (PK = 0) then 
             BEGIN
             insert into module_forms_acivity_linkage (FORM_ID, Activity_ID, IS_ACTIVE)values (FORM_ID,CODE,CHECKED);
             SELECT 'Success' msg;
             END;
            ELSE
	         BEGIN  
             update  module_forms_acivity_linkage set IS_ACTIVE = CHECKED where ID = PK;
             SELECT 'Success' msg;
             END;  
			 END IF;
 END;
 END IF;
END ;;
