CREATE DEFINER=`root`@`%` PROCEDURE `sp_userProfile_docAction_stp`(IN DOC_ACTION_ID INT)
BEGIN
SELECT IFNULL(`b`.`UPA_CODE`, 0) AS `UPA_CODE`,
        `a`.`USER_PROFILE_ID` AS `USER_PROFILE_ID`,
        `a`.`USER_PROFILE_NAME` AS `USER_PROFILE_NAME`,
        CAST(IFNULL(`b`.`STATUS`, 0) AS UNSIGNED) AS `CHECKED`,
        `b`.`DOC_ACTION_ID` AS `DOC_ACTION_ID`
    FROM
        (`clos_dev`.`user_profile` `a`
        LEFT JOIN `clos_dev`.`doc_action_userprofile_link` `b` ON (((`a`.`USER_PROFILE_ID` = `b`.`USER_PROFILE_ID`)
            AND (`b`.`DOC_ACTION_ID` = DOC_ACTION_ID))))
    WHERE
        (`a`.`STATUS_ID` = 1)
         and   (`a`.`IS_DELETED` = 0);
END ;;
