CREATE DEFINER=`root`@`%` PROCEDURE `sp_user_rights`(IN user_profile_id INT)
BEGIN

    
SELECT a.module_id 'Module_ID',a.module_name 'Module_Name',b.form_id 'Form_ID',b.form_name 'Form_Name','A' as 'Type',
USER_PROFILE_ID, IS_VIEW, IS_ADD, IS_REMOVE, IS_APPROVAL,IS_EDIT,USER_RIGHT_ID
FROM clos_dev.module a
inner join clos_dev.module_form b on a.module_id = b.module_id
left join user_rights c on c.module_id = a.module_id and b.form_id = c.form_id and c.module_type = 'A' and c.user_profile_id = user_profile_id
UNION
SELECT a.module_user_id 'Module_ID',a.module_name 'Module_Name',b.form_id 'Form_ID',b.form_name 'Form_Name','U' as 'Type',
USER_PROFILE_ID, IS_VIEW, IS_ADD, IS_REMOVE, IS_APPROVAL,IS_EDIT,USER_RIGHT_ID
FROM clos_dev.module_user a
inner join clos_dev.module_user_form b on a.module_user_id = b.module_user_id
left join user_rights c on c.module_id = a.module_user_id and b.form_id = c.form_id and c.module_type = 'U' and c.user_profile_id = user_profile_id
order by Type,Module_ID ,Form_ID;

    END ;;
