CREATE DEFINER=`root`@`%` PROCEDURE `sp_ValidatePackage`(IN APPROVAL_ID int(11),IN USER_ID int(11))
BEGIN
	DECLARE msg varchar(6000) default "";
    
    DECLARE EXIT HANDLER FOR SQLEXCEPTION 
    BEGIN 
        GET DIAGNOSTICS CONDITION 1 @p1 = RETURNED_SQLSTATE, @p2 = MESSAGE_TEXT;
        set msg = CONCAT(@p1, ':', @p2);
		SELECT msg;
    END;
    SET @PROFILE_TYPE = (select c.PROFILE_TYPE_ID from employee_profile a 
						 inner join user_profile b on a.USER_PROFILE_ID = b.USER_PROFILE_ID
						 inner join user_profile_type c on c.PROFILE_TYPE_ID = b.PROFILE_TYPE_ID
						 where a.e_id = USER_ID and status = 'A' limit 1 );
	IF (@PROFILE_TYPE is null) then     BEGIN      SET msg = CONCAT(msg,'* Please set Profile Type from Administrator.\r\n');     END;    END IF;

    SET @IFExistsORRBUS = EXISTS(SELECT * FROM orr WHERE APPROVAL_ACT_ID = APPROVAL_ID and IS_DELETED = 0 and ORR_MODEL is not null);
    SET @IFExistsORRRISK = EXISTS(SELECT * FROM orr WHERE APPROVAL_ACT_ID = APPROVAL_ID and IS_DELETED = 0 and RISK_ORR_MODEL is not null);
    SET @IFExistsEXC =  EXISTS(SELECT * FROM credit_exception a 
								inner join default_value b on a.COMPLAINT = b.VALUE_ID -- and b.VALUE_KEY = 'Y'
								 WHERE a.APPROVAL_ACT_ID = APPROVAL_ID);
    SET @IFExistsFAC =  EXISTS(SELECT * FROM customer_facility a WHERE a.APPROVAL_ACT_ID = APPROVAL_ID and IS_DELETED = 0);     
    SET @IFExistsCOL =  EXISTS(SELECT * FROM cf_collateral a WHERE a.APPROVAL_ACT_ID = APPROVAL_ID and IS_DELETED = 0);    
    
    IF (@IFExistsORRBUS = 0 and (@PROFILE_TYPE = 1 or @PROFILE_TYPE = 2)) then     BEGIN      SET msg = CONCAT(msg,'* Missing ORR Business. \r\n'); END;    END IF;
    IF (@IFExistsORRRISK = 0 and @PROFILE_TYPE = 3) then     BEGIN      SET msg = CONCAT(msg,'* Missing ORR Risk. \r\n');  END;    END IF;
    IF (@IFExistsEXC = 0) then     BEGIN      SET msg = CONCAT(msg,'* Missing Exception. \r\n');     END;    END IF;
    IF (@IFExistsFAC = 0) then     BEGIN      SET msg = CONCAT(msg,'* Missing Facility. \r\n');     END;    END IF;
    IF (@IFExistsCOL = 0) then     BEGIN      SET msg = CONCAT(msg,'* Missing Collateral. \r\n');     END;    END IF;
    IF (msg = "") then     BEGIN      SET msg = '0';     END;    END IF;
    select msg ; 
END ;;
