CREATE DEFINER=`root`@`%` PROCEDURE `updateFacSequence`(
	IN PKG_CODE int(11)
)
BEGIN
    DECLARE msg varchar(6000) default "0";
	DECLARE finished INTEGER DEFAULT 0;
    DECLARE finishedChild INTEGER DEFAULT 0;
	DECLARE CF_CODE int(11) DEFAULT 0;
    DECLARE MAIN_CF_CODE int(11) DEFAULT 0;
	Declare v_sequence varchar(50);
    Declare v_seqUpdate int(11) DEFAULT 0;
	Declare v_rootUpdate int(11) DEFAULT 0;

    -- update customer_facility set root ;
    
	-- declare cursor for employee email
	DEClARE curFac 
		CURSOR FOR 
			SELECT CF_ID FROM customer_facility where main_facility_id is null and Approval_Act_ID = PKG_CODE order by cf_id;

	-- declare NOT FOUND handler
	DECLARE CONTINUE HANDLER 
        FOR NOT FOUND SET finished = 1;



         
 DECLARE EXIT HANDLER FOR SQLEXCEPTION 
    BEGIN 
        GET DIAGNOSTICS CONDITION 1 @p1 = RETURNED_SQLSTATE, @p2 = MESSAGE_TEXT;
        set msg = CONCAT(@p1, ':', @p2);
		SELECT msg;
    END;
    
	OPEN curFac;

	getFac: LOOP
		FETCH curFac INTO CF_CODE;
		IF finished = 1 THEN 
			LEAVE getFac;
		END IF;
		-- update root 
		SET v_rootUpdate = v_rootUpdate + 1;
        update customer_Facility set root = v_rootUpdate where cf_id = CF_CODE;
        
		BLOCK2: BEGIN

				Declare curChildFac 
				CURSOR FOR 
						SELECT CF_ID FROM customer_facility where main_facility_id  = CF_CODE;
			-- declare NOT FOUND handler
						DECLARE CONTINUE HANDLER 
						FOR NOT FOUND SET finishedChild = 1;
                        
             OPEN curChildFac;
					getChildFac: LOOP
					FETCH curChildFac INTO MAIN_CF_CODE;
					IF finishedChild = 1 THEN 
						LEAVE getChildFac;
					END IF;
          -- update seq 
					SET v_seqUpdate = v_seqUpdate + 1;
					SET v_sequence = concat(v_rootUpdate,'.',v_seqUpdate);
					update customer_Facility set sequence = v_sequence,root=v_rootUpdate where cf_id = MAIN_CF_CODE;

        
          	END LOOP getChildFac;
	    CLOSE curChildFac;
    END BLOCK2;     
	
    END LOOP getFac;
	CLOSE curFac;


-- update sum section 
     	
-- Funded 
	set @Funded = (select ifnull((select sum(a.facility_requested_amount) Flimit from CUSTOMER_FACILITY a 
					inner join facility b on a.facility_id  = b.facility_id  and b.is_deleted = 0 
					inner join facility_type c on c.facility_type_id  = b.facility_type_id and c.facility_type_desc_id = 'F'
					where a.approval_Act_id = PKG_CODE and a.is_deleted = 0 and a.main_facility_id is null),0)) ;
	set @ExFunded = (select ifnull( (select sum(a.last_approved_limit) Ex_Flimit from CUSTOMER_FACILITY a 
					inner join facility b on a.facility_id  = b.facility_id  and b.is_deleted = 0 
					inner join facility_type c on c.facility_type_id  = b.facility_type_id  and c.facility_type_desc_id = 'F'
					where a.approval_Act_id = PKG_CODE and a.is_deleted = 0  and a.main_facility_id is null),0));		
-- Non funded 
	set @NonFunded = (select ifnull((select sum(a.facility_requested_amount) Flimit from CUSTOMER_FACILITY a 
					inner join facility b on a.facility_id  = b.facility_id  and b.is_deleted = 0 
					inner join facility_type c on c.facility_type_id  = b.facility_type_id and c.facility_type_desc_id = 'N'
					where a.approval_Act_id = PKG_CODE and a.is_deleted = 0 and a.main_facility_id is null),0)) ;
	set @ExNonFunded = (select ifnull( (select sum(a.last_approved_limit) Ex_Flimit from CUSTOMER_FACILITY a 
					inner join facility b on a.facility_id  = b.facility_id  and b.is_deleted = 0 
					inner join facility_type c on c.facility_type_id  = b.facility_type_id  and c.facility_type_desc_id = 'N'
					where a.approval_Act_id = PKG_CODE and a.is_deleted = 0  and a.main_facility_id is null),0));		   
 -- Overall 
		set @Overall =  (select @Funded + @NonFunded) ;
        set @ExOverall = (select @ExFunded + @ExNonFunded) ;
        
-- Ouststanding  OUTSTANDING_PRINCIPLE
        set @Outstanding = (select ifnull( (select sum(a.OUTSTANDING_PRINCIPLE) Ex_Flimit from CUSTOMER_FACILITY a 
					inner join facility b on a.facility_id  = b.facility_id  and b.is_deleted = 0 
					inner join facility_type c on c.facility_type_id  = b.facility_type_id 
					where a.approval_Act_id = PKG_CODE and a.is_deleted = 0  and a.main_facility_id is null),0));	  
     if exists (select * from approval_limits_sum where approval_act_id = PKG_CODE)   
     then begin 
		update approval_limits_sum set  EX_FUNDED = @ExFunded, FUNDED = @Funded, EX_NONFUNDED = @ExNonFunded, 
                   NONFUNDED = @NonFunded, EX_OVERALL = @ExOverall, OVERALL = @Overall,OUTSTANDING = @Outstanding, UPDATED_DATE = NOW(),  
                   UPDATED_BY = (select ifnull(updated_by,created_by) from customer_facility where approval_act_id = PKG_CODE limit 1)
                   where APPROVAL_ACT_ID = PKG_CODE;
        set msg  ='Updated Successfully';
     end ;
     else 
     begin 
         insert into approval_limits_sum (OBLIGOR_ID, APPROVAL_ACT_ID, EX_FUNDED, FUNDED, EX_NONFUNDED, NONFUNDED, EX_OVERALL, OVERALL,OUTSTANDING, CREATED_BY, CREATED_DATE)
         values ((select obligor_id from approval_activity_log where approval_act_id = PKG_CODE limit 1),
                 PKG_CODE,@ExFunded,@Funded,@ExNonFunded,@NonFunded, @ExOverall, @Overall,@Outstanding,(select CREATED_BY from customer_facility where approval_act_id = PKG_CODE limit 1),NOW());
         set msg  ='Inserted Successfully';
    end ;
     end if;
SELECT msg;

   
END ;;
