CREATE DEFINER=`root`@`%` PROCEDURE `view_get_AI_Data`(IN OBLIGOR_ID int  , ACTION INT)
BEGIN
	IF(ACTION =1) THEN
		BEGIN
			select b.Value 'Heading' ,
			ifnull((select x.AIU_ID from AI_UrbanProperty x where x.OBLIGOR_ID = OBLIGOR_ID and x.AIF_ID = b.AIF_ID ),0)'AI_ID',
			ifnull(b.AIF_ID,0)'AIF_ID',
			ifnull((select x.Area from AI_UrbanProperty x where  x.OBLIGOR_ID = OBLIGOR_ID and x.AIF_ID = b.AIF_ID ),0.00)'Val1',
			ifnull((select x.Value from AI_UrbanProperty x where x.OBLIGOR_ID = OBLIGOR_ID and x.AIF_ID = b.AIF_ID),0.00)'Val2', 
			0.00 as 'Val3' , 0.00 as 'Val4'  
			FROM clos_dev.AssetInformation_FieldType  b 
			WHERE b.Type = 1;
		END;
		END IF;
	IF(ACTION =2) THEN
		BEGIN
			select b.Value 'Heading' ,
			ifnull((select x.AIV_ID from  AI_VEHICLE x where x.AIF_ID = b.AIF_ID and x.OBLIGOR_ID = OBLIGOR_ID),0)'AI_ID',
			ifnull(b.AIF_ID,0)'AIF_ID' ,
			ifnull((select x.Number from  AI_VEHICLE x where x.AIF_ID = b.AIF_ID and x.OBLIGOR_ID = OBLIGOR_ID),0.00)'Val1',
			ifnull((select x.Make   from  AI_VEHICLE x where x.AIF_ID = b.AIF_ID and x.OBLIGOR_ID = OBLIGOR_ID),'')'Val2',
			ifnull((select x.Model  from  AI_VEHICLE x where x.AIF_ID = b.AIF_ID and x.OBLIGOR_ID = OBLIGOR_ID),0)'Val3',
			0.00 as 'Val4'
			FROM clos_dev.AssetInformation_FieldType  b 
			WHERE b.Type = 2 ; 
			END;
			END IF;
	IF(ACTION =3 ) THEN
		BEGIN
			select b.Value 'Heading' ,
			ifnull((select x.AIT_ID from  AI_Tractor x where x.AIF_ID = b.AIF_ID and x.OBLIGOR_ID = OBLIGOR_ID),0)'AI_ID',
			ifnull(b.AIF_ID,0)'AIF_ID' ,
			ifnull((select x.Number from  AI_Tractor x where x.AIF_ID = b.AIF_ID and x.OBLIGOR_ID = OBLIGOR_ID),0.00)'Val1' , 
			ifnull((select x.Make   from  AI_Tractor x where x.AIF_ID = b.AIF_ID and x.OBLIGOR_ID = OBLIGOR_ID),0)'Val2' ,
			ifnull((select x.Model  from  AI_Tractor x where x.AIF_ID = b.AIF_ID and x.OBLIGOR_ID = OBLIGOR_ID),'')'Val3' ,
			ifnull((select x.HorsePower  from  AI_Tractor x where x.AIF_ID = b.AIF_ID and x.OBLIGOR_ID = OBLIGOR_ID),0)'Val4'
			FROM clos_dev.AssetInformation_FieldType  b 
			WHERE b.Type = 3; 
			END;
			END IF;
	IF(ACTION=4) THEN
		BEGIN
			select b.Value 'Heading' ,
			ifnull(( select x.AII_ID from  AI_Implements x where x.AIF_ID = b.AIF_ID and x.OBLIGOR_ID = OBLIGOR_ID),0)'AI_ID',
			ifnull(b.AIF_ID,0)'AIF_ID' ,
			ifnull((select x.Number from  AI_Implements x where x.AIF_ID = b.AIF_ID and x.OBLIGOR_ID = OBLIGOR_ID),0.00)'Val1', 
			0.00 as'Val2'  ,
			0.00 as 'Val3' ,
			0.00 as'val4'
		FROM clos_dev.AssetInformation_FieldType  b 
		WHERE b.Type = 4 ; 
		END ;
		END IF;
END ;;
