CREATE DEFINER=`root`@`%` PROCEDURE `view_Non_Finantial`(IN x_OBLIGOR_ID int)
BEGIN  



CREATE TEMPORARY TABLE NON_FINANCIAL_INFO_temp
select ifnull(b.NFI_ID,0)'NFI_ID',ifnull(c.NIF_ID,0)'NIF_ID',c.Value,ifnull(b.OBLIGOR_ID,0)'OBLIGOR_ID',ifnull(b.Year1,0)'Year1',ifnull(b.Val1,'')'Val1',ifnull(b.Year2,0)'Year2',ifnull(b.Val2,'')'Val2'
from  clos_dev.NON_FINANCIAL_INFO b 
right JOIN clos_dev.NON_FINANCIAL_INFO_fieldtype c
ON  c.NIF_ID = b.NIF_ID
and b.OBLIGOR_ID = x_OBLIGOR_ID ; 

-- select * from NON_FINANCIAL_INFO_temp ; 
update NON_FINANCIAL_INFO_temp 
set  OBLIGOR_ID = x_OBLIGOR_ID  ;

update NON_FINANCIAL_INFO_temp 
set year2 =   1 ; 

update NON_FINANCIAL_INFO_temp 
set val2 =  ( select distinct(a.EMPLOYEES) 
from obligor_business_details a, obligor_customer_rating b , credit_rating_agency d
where b.AGENCY_ID = d.AGENCY_ID
and   a.OBLIGOR_ID = b.OBLIGOR_ID   
and   b.OBLIGOR_ID = 229 
order by d.AGENCY_ID  ) 
where NIF_ID = 1 ;

update NON_FINANCIAL_INFO_temp 
set val2 =  ifnull(( select distinct (a.NO_OF_BRANCHES) 
from obligor_business_details a, obligor_customer_rating b , credit_rating_agency d
where b.AGENCY_ID = d.AGENCY_ID
and   a.OBLIGOR_ID = b.OBLIGOR_ID   
and   b.OBLIGOR_ID = x_OBLIGOR_ID 
order by d.AGENCY_ID  ),0) 
where NIF_ID = 2 ;


update NON_FINANCIAL_INFO_temp 
set val2 =  (select CONCAT(group_concat(d.AGENCY_Name separator ',' ),"-",group_concat( CASE
    WHEN b.LONG_TERM = 1 THEN "AAA"
    WHEN b.LONG_TERM = 2 THEN "AA+"
    WHEN b.LONG_TERM = 3 THEN "AA-"
    WHEN b.LONG_TERM = 4 THEN "A+"
    WHEN b.LONG_TERM = 5 THEN "BBB+"
    WHEN b.LONG_TERM = 6 THEN "BBB"
    WHEN b.LONG_TERM = 7 THEN "BBB-"
    WHEN b.LONG_TERM = 8 THEN "BB+BB"
    WHEN b.LONG_TERM = 9 THEN "BB-"
    WHEN b.LONG_TERM = 10 THEN "B+"
    WHEN b.LONG_TERM = 11 THEN "CCC+"
    WHEN b.LONG_TERM = 12 THEN "CCC"
    WHEN b.LONG_TERM = 13 THEN "D"
    WHEN b.LONG_TERM = 14 THEN "C"
    ELSE "Not Defined" END separator ',' ))
from obligor_business_details a, obligor_customer_rating b , credit_rating_agency d
where b.AGENCY_ID = d.AGENCY_ID
and   a.OBLIGOR_ID = b.OBLIGOR_ID   
and   b.OBLIGOR_ID = x_OBLIGOR_ID 
order by d.AGENCY_ID  ) 
where NIF_ID = 3 ;


update NON_FINANCIAL_INFO_temp 
set val2 = ( select CONCAT(group_concat(d.AGENCY_Name separator ',' ),"-",group_concat( CASE
    WHEN b.SHORT_TERM = 1 THEN "A1+"
    WHEN b.SHORT_TERM = 2 THEN "A1"
    WHEN b.SHORT_TERM = 3 THEN "A2"
    WHEN b.SHORT_TERM = 4 THEN "A3"
    WHEN b.SHORT_TERM = 5 THEN "Others"
    WHEN b.SHORT_TERM = 6 THEN "C"
    ELSE "Not Defined" END separator ',' ))
from obligor_business_details a, obligor_customer_rating b , credit_rating_agency d
where b.AGENCY_ID = d.AGENCY_ID
and   a.OBLIGOR_ID = b.OBLIGOR_ID   
and   b.OBLIGOR_ID = x_OBLIGOR_ID 
order by d.AGENCY_ID  ) 
where NIF_ID = 4 ;


update NON_FINANCIAL_INFO_temp 
set val2 = (select  CONCAT(group_concat(d.AGENCY_Name separator ',' ),"-",group_concat( CASE
    WHEN b.SHORT_TERM = 1 THEN "A1+"
    WHEN b.SHORT_TERM = 2 THEN "A1"
    WHEN b.SHORT_TERM = 3 THEN "A2"
    WHEN b.SHORT_TERM = 4 THEN "A3"
    WHEN b.SHORT_TERM = 5 THEN "Others"
    WHEN b.SHORT_TERM = 6 THEN "C"
    ELSE "Not Defined" END separator ',' ))
from obligor_business_details a, obligor_customer_rating b , credit_rating_agency d
where b.AGENCY_ID = d.AGENCY_ID
and   a.OBLIGOR_ID = b.OBLIGOR_ID   
and   b.OBLIGOR_ID = x_OBLIGOR_ID 
order by d.AGENCY_ID  ) 
where NIF_ID = 5;


update NON_FINANCIAL_INFO_temp 
set Year1 =  ifnull(( select Year1 
from NON_FINANCIAL_INFO
where OBLIGOR_ID = x_OBLIGOR_ID 
and  NIF_ID = 1  ),0) 
where OBLIGOR_ID = x_OBLIGOR_ID ;

update NON_FINANCIAL_INFO_temp 
set Val1 =  ifnull(( select Val1 
from NON_FINANCIAL_INFO
where OBLIGOR_ID = x_OBLIGOR_ID 
and  NIF_ID = 1  ),0) 
where OBLIGOR_ID = x_OBLIGOR_ID 
and NIF_ID = 1 ; 

update NON_FINANCIAL_INFO_temp 
set Val1 =  ifnull(( select Val1 
from NON_FINANCIAL_INFO
where OBLIGOR_ID = x_OBLIGOR_ID 
and  NIF_ID = 2  ),0) 
where OBLIGOR_ID = x_OBLIGOR_ID 
and NIF_ID = 2 ;


update NON_FINANCIAL_INFO_temp 
set Val1 =  ifnull(( select Val1 
from NON_FINANCIAL_INFO
where OBLIGOR_ID = x_OBLIGOR_ID 
and  NIF_ID = 3  ),'') 
where OBLIGOR_ID = x_OBLIGOR_ID 
and NIF_ID = 3 ;

update NON_FINANCIAL_INFO_temp 
set Val1 =  ifnull(( select Val1 
from NON_FINANCIAL_INFO
where OBLIGOR_ID = x_OBLIGOR_ID 
and  NIF_ID = 4  ),'') 
where OBLIGOR_ID = x_OBLIGOR_ID 
and NIF_ID = 4 ;

update NON_FINANCIAL_INFO_temp 
set Val1 =  ifnull(( select Val1 
from NON_FINANCIAL_INFO
where OBLIGOR_ID = x_OBLIGOR_ID 
and  NIF_ID = 5  ),'') 
where OBLIGOR_ID = x_OBLIGOR_ID 
and NIF_ID = 5 ;


select * from NON_FINANCIAL_INFO_temp ; 
drop table NON_FINANCIAL_INFO_temp ; 

END ;;
