CREATE DEFINER=`root`@`%` PROCEDURE `wf_user_IPR_update`(IN IPR_ACT_ID INT , WF_PROFILE_ID_NEW int , COMMENT_User text ,
																OTHER_REMARKS text , Action_Taken char(1),WF_ACTION_ID bigint,E_ID int ,
                                                                ASSIGNED_EMP_ID INT)
BEGIN

 DECLARE RevertEmp int(11) default 0;
 
    DECLARE errno INT;
    DECLARE EXIT HANDLER FOR SQLEXCEPTION
    BEGIN
    GET CURRENT DIAGNOSTICS CONDITION 1 errno = MYSQL_ERRNO;
    SELECT errno AS MYSQL_ERROR;
    ROLLBACK;
    END;
	-- select * from IPR_activity_log ; 
	START TRANSACTION;
  
  
  set @IPR_ACT_ID2 := ( select x.IPR_ACT_ID from IPR_activity_log x where  x.IPR_ID = IPR_ACT_ID limit 1  );

  set @WF_ProfileID_OLD = (select x.WF_PROFILE_ID from IPR_activity_log x where  x.IPR_ACT_ID = @IPR_ACT_ID2 );

Update IPR_activity_log SET  LAST_ACT_DATE  = NOW() ,
							  WF_PROFILE_ID = WF_PROFILE_ID_NEW 
WHERE IPR_ID = IPR_ACT_ID ; 

set @sequence  = (SELECT COUNT(*) FROM wf_user_package_IPR_log x where x.IPR_ACT_ID = @IPR_ACT_ID2  );
set @LogID = (select max(x.LOG_ID) from wf_user_package_IPR_log x where  x.IPR_ACT_ID = @IPR_ACT_ID2   );

set RevertEmp =  ( select x.E_ID  from wf_user_package_IPR_log x 
					where x.IPR_ACT_ID = @IPR_ACT_ID2 and  x.NEW_WF_PROFILE_ID = WF_PROFILE_ID_NEW limit 1 ); 
 
IF (Action_Taken = 'R' and ASSIGNED_EMP_ID = 0 )then
begin
 
update wf_user_package_IPR_log set COMMENT = COMMENT_User ,
										 OTHER_REMARKS   =   OTHER_REMARKS ,
                                         WF_ACTION_ID    =   WF_ACTION_ID , 
                                         E_ID		     =   E_ID ,
                                         ASSIGNED_EMP_ID =   RevertEmp 
WHERE LOG_ID = @LogID;
 
 
 	UPDATE wf_IPR_package_chain_log 
				SET CURRENT_AT = 0 
					AND LOG_ID = @LogID  ; 
 
insert wf_user_package_IPR_log (OLD_WF_PROFILE_ID,NEW_WF_PROFILE_ID,ACTION_DATE,COMMENT,OTHER_REMARKS,
									Action_Taken,SEQUENCE,WF_ACTION_ID,E_ID,IPR_ACT_ID)
                                    VALUES( @WF_ProfileID_OLD,
											WF_PROFILE_ID_NEW,
											NOW(),
											null,
											null,
											Action_Taken,
											@sequence+1,
											NULL,
											NULL,
											@IPR_ACT_ID2
											);

	

set @LogID2 = LAST_INSERT_ID() ; 

insert wf_IPR_package_chain_log (ASSIGNED_EMP_ID , DATEOFLANDING , CURRENT_AT , LOG_ID , IPR_ACT_ID)
				VALUES (RevertEmp, NOW(),1,@LogID2 ,@IPR_ACT_ID2); 

    select @IPR_ACT_ID2 , WF_PROFILE_ID_NEW  , Action_Taken , WF_ACTION_ID ,E_ID , RevertEmp  ; 
    
 end;
 else
 begin
--  select 1  ;
 update wf_user_package_IPR_log set COMMENT = COMMENT_User ,
										 OTHER_REMARKS   =   OTHER_REMARKS ,
                                         WF_ACTION_ID    =   WF_ACTION_ID , 
                                         E_ID		     =   E_ID ,
                                         ASSIGNED_EMP_ID =   ASSIGNED_EMP_ID 
WHERE LOG_ID = @LogID;

		UPDATE wf_IPR_package_chain_log 
			SET CURRENT_AT = 0 
		WHERE LOG_ID = @LogID  ;

insert wf_user_package_IPR_log (OLD_WF_PROFILE_ID,NEW_WF_PROFILE_ID,ACTION_DATE,COMMENT,OTHER_REMARKS,
									Action_Taken,SEQUENCE,WF_ACTION_ID,E_ID,IPR_ACT_ID)
                                    VALUES( @WF_ProfileID_OLD,
											WF_PROFILE_ID_NEW,
											NOW(),
											null,
											null,
											Action_Taken,
											@sequence+1,
											NULL,
											NULL,
											@IPR_ACT_ID2
											);

                    
set @LogID2 = LAST_INSERT_ID() ;  

insert wf_IPR_package_chain_log (ASSIGNED_EMP_ID , DATEOFLANDING , CURRENT_AT , LOG_ID , IPR_ACT_ID)
				VALUES (ASSIGNED_EMP_ID, NOW(),1,@LogID2 ,@IPR_ACT_ID2);
                    --   select  6 ;  
	select @IPR_ACT_ID2 , WF_PROFILE_ID_NEW  , Action_Taken , WF_ACTION_ID ,E_ID , ASSIGNED_EMP_ID  ; 
  
end;
end if;
    COMMIT WORK;
END ;;
