BEGIN
DECLARE errno INT ;
DECLARE Action_Taken3 char(1) ;
DECLARE ID BIGINT ;
DECLARE EXIT HANDLER FOR SQLEXCEPTION
BEGIN
GET CURRENT DIAGNOSTICS CONDITION 1 errno = MYSQL_ERRNO;
SELECT errno AS MYSQL_ERROR;
ROLLBACK;
END;
START TRANSACTION;
SET @Action_Taken2 = Action_Taken;
SET Action_Taken3 =Action_Taken ;
SET @Activity_Key = (select activity_key from wf_activity_setup a inner join approval_activity_log b on b.current_activity_id = a.activity_id where b.approval_act_id = APPROVAL_ACT_ID2);set @WF_PROFILE_ID_OLD = (select x.WF_PROFILE_ID from approval_activity_log x where x.APPROVAL_ACT_ID = APPROVAL_ACT_ID2);IF (@Activity_Key = 'FAI')then
BEGIN
set @WF_PROFILE_ID_NEW = ( select To_Profile from wf_decidingactions where WF_PROFILE_ID =@WF_PROFILE_ID_OLD and Action_Taken = 'A' limit 1 );
set @WF_ACTIONID = ( select WF_ACTION_ID from wf_decidingactions where WF_PROFILE_ID =@WF_PROFILE_ID_OLD and Action_Taken = 'A' limit 1 );
END;
ELSE BEGIN
set @WF_PROFILE_ID_NEW = ( select To_Profile from wf_decidingactions where WF_PROFILE_ID =@WF_PROFILE_ID_OLD and Action_Taken != 'A' limit 1 );
set @WF_ACTIONID = ( select WF_ACTION_ID from wf_decidingactions where WF_PROFILE_ID =@WF_PROFILE_ID_OLD and Action_Taken = 'A' limit 1 );END;
END IF;
set @CURRENT_ACTIVITY_ID = (select activity_id from wf_activity_setup where activity_key = concat(left(@Activity_Key,2) , @Action_Taken2));
-- select @CURRENT_ACTIVITY_ID ;
set @LAST_ACT_DATE = NOW();/*
select LAST_ACT_DATE , CURRENT_ACTIVITY_ID , ACT_STATUS , (SELECT A.LOGIN_ID FROM employee_credentials A WHERE A.E_ID = E_ID LIMIT 1)'LOGIN ID'
from approval_activity_log
WHERE APPROVAL_ACT_ID = APPROVAL_ACT_ID2;
*/
Update approval_activity_log SET LAST_ACT_DATE = @LAST_ACT_DATE,
CURRENT_ACTIVITY_ID = @CURRENT_ACTIVITY_ID,
ACT_STATUS = @Action_Taken2,
APPROVED_BY = (SELECT A.LOGIN_ID FROM employee_credentials A WHERE A.E_ID = E_ID LIMIT 1)
WHERE APPROVAL_ACT_ID = APPROVAL_ACT_ID2;set @sequence = (SELECT COUNT(*) FROM wf_user_package_approval_log x where x.APPROVAL_ACT_ID = APPROVAL_ACT_ID2);
set @LogID = (select max(x.LOG_ID) from wf_user_package_approval_log x where x.APPROVAL_ACT_ID = APPROVAL_ACT_ID2);
-- select @sequence ,@LogID , @WF_ACTIONID, COMMENT_User , OTHER_REMARKS ;
update wf_user_package_approval_log
set COMMENT = COMMENT_User,
OTHER_REMARKS = OTHER_REMARKS,
E_ID = E_ID,
ASSIGNED_EMP_ID = E_ID,
WF_ACTION_ID = @WF_ACTIONID
where APPROVAL_ACT_ID = APPROVAL_ACT_ID2 and LOG_ID = @LogID;
INSERT wf_user_package_approval_log (OLD_WF_PROFILE_ID,NEW_WF_PROFILE_ID,ACTION_DATE,COMMENT,OTHER_REMARKS, Action_Taken,SEQUENCE,WF_ACTION_ID,E_ID,APPROVAL_ACT_ID)
VALUES( @WF_PROFILE_ID_OLD, @WF_PROFILE_ID_NEW, NOW(), null, null, Action_Taken, @sequence+1,@WF_ACTIONID, NULL, APPROVAL_ACT_ID2);UPDATE wf_user_package_chain_log SET CURRENT_AT = 0 WHERE APPROVAL_ACT_ID = APPROVAL_ACT_ID2;
SELECT  'Success' as Status , APPROVAL_ACT_ID2 'APPROVAL_ACT_ID2';
COMMIT WORK;

END ;;
