CREATE DEFINER=`root`@`%` PROCEDURE `wf_user_package_update`(IN APPROVAL_ACT_ID INT , WF_PROFILE_ID_NEW int , COMMENT_User text ,
																OTHER_REMARKS text , Action_Taken char(1),WF_ACTION_ID bigint,E_ID int ,
                                                                ASSIGNED_EMP_ID INT)
BEGIN


 DECLARE APPROVAL_ACT_ID2 int(11) default 0;
 DECLARE RevertEmp int(11) default 0;
 
    DECLARE errno INT;
    DECLARE EXIT HANDLER FOR SQLEXCEPTION
    BEGIN
    GET CURRENT DIAGNOSTICS CONDITION 1 errno = MYSQL_ERRNO;
    SELECT errno AS MYSQL_ERROR;
    ROLLBACK;
    END;
	
	START TRANSACTION;
  
  set APPROVAL_ACT_ID2 = APPROVAL_ACT_ID ;
   set @WF_ProfileID_OLD = (select x.WF_PROFILE_ID from approval_activity_log x where  x.APPROVAL_ACT_ID = APPROVAL_ACT_ID2 );

Update approval_activity_log SET  LAST_ACT_DATE= NOW() ,
								  WF_PROFILE_ID = WF_PROFILE_ID_NEW 
WHERE APPROVAL_ACT_ID = APPROVAL_ACT_ID2 ; 

set @sequence  = (SELECT COUNT(*) FROM wf_user_package_approval_log x where x.APPROVAL_ACT_ID = APPROVAL_ACT_ID2  );
set @LogID = (select max(x.LOG_ID) from wf_user_package_approval_log x where  x.APPROVAL_ACT_ID = APPROVAL_ACT_ID2   );
-- select * from wf_user_package_approval_log ; 

set RevertEmp =  ( select x.E_ID  from wf_user_package_approval_log x 
					where x.APPROVAL_ACT_ID = APPROVAL_ACT_ID2 and  x.NEW_WF_PROFILE_ID = WF_PROFILE_ID_NEW limit 1 ); 
 -- select 0  ;

IF (Action_Taken = 'R' and ASSIGNED_EMP_ID = 0 )then
begin
-- select 1  ;

update wf_user_package_approval_log set COMMENT = COMMENT_User ,
										 OTHER_REMARKS   =   OTHER_REMARKS ,
                                         WF_ACTION_ID    =   WF_ACTION_ID , 
                                         E_ID		     =   E_ID ,
                                         ASSIGNED_EMP_ID =   RevertEmp ,
                                         TAT = get_TAT(ACTION_DATE)
where APPROVAL_ACT_ID = APPROVAL_ACT_ID2 and LOG_ID = @LogID;
 -- select 2  ;
insert wf_user_package_approval_log (OLD_WF_PROFILE_ID,NEW_WF_PROFILE_ID,ACTION_DATE,COMMENT,OTHER_REMARKS,
									Action_Taken,SEQUENCE,WF_ACTION_ID,E_ID,APPROVAL_ACT_ID)
                                    VALUES( @WF_ProfileID_OLD,
											WF_PROFILE_ID_NEW,
											NOW(),
											null,
											null,
											Action_Taken,
											@sequence+1,
											NULL,
											NULL,
											APPROVAL_ACT_ID2
											);

		UPDATE wf_user_package_chain_log 
				SET CURRENT_AT = 0 
					WHERE APPROVAL_ACT_ID = APPROVAL_ACT_ID2 
					AND LOG_ID = @LogID  ; 
 -- select 3  ;
set @LogID2 = (select max(LOG_ID) from wf_user_package_approval_log where  APPROVAL_ACT_ID = APPROVAL_ACT_ID2 );   
 --  select 4  ;
insert wf_user_package_chain_log (ASSIGNED_EMP_ID , DATEOFLANDING , CURRENT_AT , LOG_ID , APPROVAL_ACT_ID)
				VALUES (RevertEmp, NOW(),1,@LogID2 ,APPROVAL_ACT_ID2); 
	-- select 5; 
    select @APPROVAL_ACT_ID2 , WF_PROFILE_ID_NEW  , Action_Taken , WF_ACTION_ID ,E_ID , RevertEmp  ; 
    
 end;
 else
 begin
 update wf_user_package_approval_log set COMMENT = COMMENT_User ,
										 OTHER_REMARKS   =   OTHER_REMARKS ,
                                         WF_ACTION_ID    =   WF_ACTION_ID , 
                                         E_ID		     =   E_ID ,
                                         ASSIGNED_EMP_ID =   ASSIGNED_EMP_ID ,
                                         TAT = get_TAT(ACTION_DATE)
where APPROVAL_ACT_ID = APPROVAL_ACT_ID2 and LOG_ID = @LogID;
insert wf_user_package_approval_log (OLD_WF_PROFILE_ID,NEW_WF_PROFILE_ID,ACTION_DATE,COMMENT,OTHER_REMARKS,
